type SplitTitleProps = {
  className?: string;     // Additional class for component container
  spread?: number;        // Value in pixels identifying the radius of splits to fly around
  fadeDuration?: number;  // Amount if seconds fade duration will take
  stagger?: number;       // Child staggering amount (small timeout before every children that offsets animation start)
  interval?: number;      // Amount of seconds between fadeIn animation finish and fadeOut animation start
};

const SplitTitle: FunctionComponent<SplitTitleProps> = ({
  className,
  spread = 100,
  fadeDuration: duration = 1,
  stagger = 0.05,
  interval = 1,
}) => {
  const [isFirstLaunch, setFirstLaunch] = useState(true);
  const [isVisible, setVisible] = useState(true);
  const [currentWordIdx, setCurrentWordIdx] = useState(0);

  const currentWord = words[currentWordIdx]
  const currentGeom = svgAttributes[currentWord]

  const getVariants = () => ({
    visible: { 
      opacity: 1, 
      y: 0,
      x: 0,
      rotate: 0, 
      transition: {
        duration: duration, 
      },
    },
    hidden: {
      opacity: 0,
      y: spread * 2 * Math.random() - spread,
      x: spread * 2 * Math.random() - spread,
      rotate: 180 + 360 * Math.random(), 
      transition: {
        duration: duration, 
      },
    },
  });

  useEffect(() => {
    // This effects allow for initial interval timeout
    let timeout = setTimeout(() => {
      setFirstLaunch(false);
    }, interval * 1000);

    return () => {
      clearTimeout(timeout);
    }
  }, [])
  
  return (
    
    <div className={`${styles.container} ${className || ''}`}>
      <motion.svg
        role="img"
        aria-label={`Animated text showing the word: ${currentWord}`}
        transition={{  
          staggerChildren: stagger
        }}
        initial={isVisible ? "visible" : "hidden"}
        animate={isVisible && !isFirstLaunch ? "hidden" : "visible"}
        
        onAnimationComplete={(definition: "visible" | "hidden") => {
          if (definition === "hidden") {
            setCurrentWordIdx((currentWordIdx + 1) % words.length);
            setVisible(false);
          } else {
            let timeout = setTimeout(() => setVisible(true), interval * 1000)
            return () => { window.clearTimeout(timeout) };
          }
        }}
        width={`${currentGeom.size[0]}`} 
        height={`${currentGeom.size[1]}`} 
        viewBox={`0 0 ${currentGeom.size[0]} ${currentGeom.size[1]}`} 
        xmlns="http://www.w3.org/2000/svg" 
        overflow="visible">
        
        <defs>
          { currentGeom.geometry }
        </defs>
        {          
          currentGeom.splits.map((item, i) => (
              <motion.use 
                key={`path_${currentWord}_${i}`}
                style={{
                  originX: `${item.center[0]}px`,
                  originY: `${item.center[1]}px`,
                }}
                variants={getVariants()}
                clipPath={`path('${item.path}')`}
                href={`#${currentWord}`}
                />
          ))
        }
      </motion.svg>
    </div>
  );
};
export default SplitTitle;
